import React from "react";
import {Row, Col, Button, Form, Accordion} from "react-bootstrap";
const Todo = ({ todo, onTodoDelete, onTodoUpdate }) => {

    const [completed, setCompleted] = React.useState(todo.completed);

    const onDelete = ()=>{
        onTodoDelete(todo);
    }
    const toggleCompletion = () => {
        /**
         * front-end の変数の設定
         * todo.completedの値にフロントで反転させたcompletedを入れる
         */
        onTodoUpdate({...todo, completed: !completed});
        setCompleted(!completed);
    }
    return (
        <>
            <Accordion>
                <Accordion.Item eventKey={todo.todo_id}>
                <Accordion.Header>
                <Form>
                    <Form.Check
                        type="checkbox" 
                        checked={completed} 
                        onChange={toggleCompletion}
                        />
                </Form>
                <Row>
                    <Col xs={10} className={completed ? 'completed': "" } >
                        {todo.title}
                    </Col>
                    <Col>
                        <Button variant="link" onClick={onDelete}>
                        <i className="fa fa-remove text-danger" />
                    </Button>
                    </Col>
                </Row>
                </Accordion.Header>
                <Accordion.Body>
                    {todo.content}
                </Accordion.Body>
                </Accordion.Item>
            </Accordion>
        </>
    )
}

export default Todo;
