import React from "react";
import {Row, Col, Button, Form, Accordion ,Container} from "react-bootstrap";
const Todo = ({ todo, onTodoDelete, onTodoUpdate }) => {

    const [completed, setCompleted] = React.useState(todo.completed);

    const onDelete = ()=>{
        onTodoDelete(todo);
    }
    const toggleCompletion = () => {
        /**
         * front-end の変数の設定
         * todo.completedの値にフロントで反転させたcompletedを入れる
         */
        onTodoUpdate({...todo, completed: !completed});
        setCompleted(!completed);
    }
    return (
        <>
        <Container>
            <Row>
                <Col xs={1}>
                    <Form>
                        <Form.Check
                        type="checkbox" 
                        checked={completed} 
                        onChange={toggleCompletion}
                        />
                    </Form>
                </Col>
                <Col xs={1}>
                <Button variant="link" onClick={onDelete}>
                        <i className="fa fa-remove text-danger" />
                </Button>
                </Col>
                <Col>
                <Accordion>
                    <Accordion.Item eventKey={todo.todo_id}>
                        <Accordion.Header>
                        <Row>
                            <Col  className={completed ? 'completed': "" } >
                                        {todo.title}
                            </Col>
                        </Row>
                        </Accordion.Header>
                        <Accordion.Body>
                            <Row>
                                <Col className={completed ? 'completed': "" } >
                                    {todo.content}
                                </Col>
                            </Row>
                        </Accordion.Body>
                    </Accordion.Item>
                </Accordion>
                </Col>

            </Row>
        </Container>

        </>
    )
}

export default Todo;
