import { useState, useEffect } from "react";
import axios from 'axios';
import TodoInput from "./TodoInput";
import Todo from "./Todo";

const BASE_URL = "http://localhost:3333/api";


const TodoList = () => {
const [todos, setTodos] = useState(null);
    useEffect(() => {
        // サーバー側で CORS 対策していないと取得できない。
        axios.get(BASE_URL + '?user_id=1')
        .then((res) => setTodos(res.data))
    },[]);

    const onItemCreate = async (newItem) =>{

        // ただのjson body が空になる。ので以下を使用
        const params = new URLSearchParams();
        params.append('user_id', 1);
        params.append('text', newItem);
        params.append('complteted', false);

        // Send POST request;
        const result = await axios.post(BASE_URL, params);
        setTodos([...todos, 
            {text: newItem, completed: false, uuid: result.data.uuid}])
        // Update my front-end
    }


    const onTodoDelete = async (todo) => {
        // update frondt-end
        await axios.delete(`${BASE_URL}/${todo.uuid}`);
        const index = todos.findIndex((i) => i.uuid  === todo.uuid);
        console.log(todos);
        setTodos([...todos.slice(0, index), ...todos.slice(index + 1 )]);
        console.log(todos);
    }
    // 空の場合はLoading
    // useEffect が変数への格納を監視している。
    if (todos === null) return <div>Loading</div>;

    return (
        <div>
            {todos.map(( todo ) => (
                <Todo key={todo.uuid} todo={todo} onTodoDelete={onTodoDelete} />
                )
            )}
            <TodoInput onItemCreate={onItemCreate} />
        </div>
    )
};

export default TodoList;
