import { useState, useEffect } from "react";
import axios from 'axios';
import TodoInput from "./TodoInput";

const BASE_URL = "http://localhost:3333/api";

axios.defaults.headers.common['Content-Type'] = 'application/json';


const TodoList = () => {
const [todos, setTodos] = useState(null);
    useEffect(() => {
        // サーバー側で CORS 対策していないと取得できない。
        axios.get(BASE_URL + '?user_id=1')
        .then((res) => setTodos(res.data))
    },[]);

    const onItemCreate = (newItem) =>{
        // Send POST request;
        console.log({user_id: 1, text: newItem , compltete: false});
        axios.post(BASE_URL, {user_id: 1, text: newItem , compltete: false} ,{ headers: { "Content-type": "text/plain" } });
        // Update my front-end
    }
    // 空の場合はLoading
    // useEffect が変数への格納を監視している。
    if (todos === null) return <div>Loading</div>;

    return (
        <div>
            {todos.map(( todo ) => (
                <div key={todo.todo_id}>{todo.text}</div>
                )
            )}
            <TodoInput onItemCreate={onItemCreate} />
        </div>
    )
};

export default TodoList;
